<?phpif(isset($_GET['rrd']))    $rrdFile = dirname(__FILE__) . "/rra/" . $_GET['rrd'] . ".rrd";else    throw new Exception ('Not a valid request');    $request = json_decode(file_get_contents('php://input'));$updater = new RRDUpdater($rrdFile);if (is_array($request)){    foreach($request as $key=>$val)     {        print_r(objectToArray($val));        //echo "exec: updater->update({$val}, ".date('U') .");\n";        $updater->update(objectToArray($val), date('U'));    }}else    $updater->update(array($request->name => $request->value), $request->timestamp);// $result = rrd_fetch($rrdFile, array( "AVERAGE", "--resolution", "60") );// print_r($result);//http://www.if-not-true-then-false.com/2009/php-tip-convert-stdclass-object-to-multidimensional-array-and-convert-multidimensional-array-to-stdclass-object/function objectToArray($d) {		if (is_object($d)) {			// Gets the properties of the given object			// with get_object_vars function			$d = get_object_vars($d);		} 		if (is_array($d)) {			/*			* Return array converted to object			* Using __FUNCTION__ (Magic constant)			* for recursive call			*/			return array_map(__FUNCTION__, $d);		}		else {			// Return array			return $d;		}	}?>